// Simulate Nano Banana AI artwork generation
export async function generateArtwork(drawingData, prompt) {
  // Extract object type from prompt
  const objectType = extractObjectFromPrompt(prompt);
  
  // Simulate processing time
  await new Promise(resolve => setTimeout(resolve, 2000 + Math.random() * 2000)); // 2-4 seconds
  
  // Get appropriate artwork or fallback to default
  const artwork = mockArtwork[objectType.toLowerCase()] || mockArtwork.default;
  
  return {
    success: true,
    imageUrl: artwork.imageUrl,
    style: artwork.style,
    description: artwork.description,
    originalPrompt: prompt,
    processingTime: Math.round(2000 + Math.random() * 2000), // ms
    aiProvider: 'Nano Banana AI',
    enhancement: {
      colorCorrection: true,
      styleTransfer: true,
      detailEnhancement: true,
      artisticFilter: artwork.style
    }
  };
}

// Simulate ElevenLabs background music generation
export async function generateBackgroundMusic(drawingData, prompt) {
  const objectType = extractObjectFromPrompt(prompt);
  
  // Simulate processing time
  await new Promise(resolve => setTimeout(resolve, 1500 + Math.random() * 1000)); // 1.5-2.5 seconds
  
  const music = mockMusicThemes[objectType.toLowerCase()] || mockMusicThemes.default;
  
  return {
    success: true,
    audioUrl: music.audioUrl, // null in demo mode
    theme: music.theme,
    description: music.description,
    duration: 30, // seconds
    aiProvider: 'ElevenLabs',
    generationParams: {
      mood: 'calm-inspiring',
      tempo: 'medium',
      instruments: ['ambient', 'acoustic'],
      theme: objectType
    }
  };
}

// Combined AI processing function
export async function processDrawingWithAI(drawingData, prompt) {
  try {
    // Process artwork and music in parallel
    const [artworkResult, musicResult] = await Promise.all([
      generateArtwork(drawingData, prompt),
      generateBackgroundMusic(drawingData, prompt)
    ]);
    
    return {
      success: true,
      artwork: artworkResult,
      music: musicResult,
      totalProcessingTime: Math.max(
        artworkResult.processingTime,
        musicResult ? 1500 + Math.random() * 1000 : 0
      ),
      timestamp: new Date().toISOString()
    };
    
  } catch (error) {
    console.error('AI processing error:', error);
    
    return {
      success: false,
      error: 'Failed to process drawing with AI',
      fallback: {
        artwork: mockArtwork.default,
        music: mockMusicThemes.default
      }
    };
  }
}

// Helper function to extract object from prompt
function extractObjectFromPrompt(prompt) {
  const words = prompt.toLowerCase().split(' ');
  const articles = ['a', 'an', 'the', 'draw', 'please', 'some'];
  return words.find(word => !articles.includes(word) && word.length > 2) || 'object';
}

// Generate random drawing prompts
export function generateRandomPrompt() {
  const prompts = [
    'Draw a fish',
    'Draw a cat',
    'Draw a house', 
    'Draw a tree',
    'Draw a car',
    'Draw a flower',
    'Draw a bird',
    'Draw a sun',
    'Draw a butterfly',
    'Draw a dog',
    'Draw a boat',
    'Draw a mountain'
  ];
  
  return prompts[Math.floor(Math.random() * prompts.length)];
}

// Validate drawing matches prompt (basic heuristics)
export function validateDrawingContent(drawingData, prompt) {
  const objectType = extractObjectFromPrompt(prompt);
  const { strokes } = drawingData;
  
  if (!strokes || strokes.length === 0) {
    return { isValid: false, reason: 'No drawing detected' };
  }
  
  // Basic validation based on stroke count and complexity
  const strokeCount = strokes.length;
  const totalPoints = strokes.reduce((sum, stroke) => sum + stroke.points.length, 0);
  
  let minStrokes = 2;
  let maxStrokes = 20;
  
  // Object-specific expectations
  switch (objectType.toLowerCase()) {
    case 'fish':
      minStrokes = 2; // body + tail minimum
      break;
    case 'cat':
      minStrokes = 3; // body + head + ears minimum
      break;
    case 'house':
      minStrokes = 3; // walls + roof + door minimum
      break;
    case 'tree':
      minStrokes = 2; // trunk + foliage minimum
      break;
    case 'car':
      minStrokes = 3; // body + wheels minimum
      break;
    default:
      minStrokes = 1;
  }
  
  if (strokeCount < minStrokes) {
    return { 
      isValid: false, 
      reason: `Drawing seems incomplete. Expected at least ${minStrokes} strokes for a ${objectType}` 
    };
  }
  
  if (strokeCount > maxStrokes) {
    return { 
      isValid: false, 
      reason: 'Drawing seems overly complex or repetitive' 
    };
  }
  
  if (totalPoints < 10) {
    return { 
      isValid: false, 
      reason: 'Drawing is too simple' 
    };
  }
  
  return { 
    isValid: true, 
    confidence: Math.min(95, 60 + (strokeCount * 5)), 
    analysis: `Reasonable ${objectType} drawing with ${strokeCount} strokes` 
  };
}